script:
let currentUser = frappe.session.user;

let userNameElement = root_element.querySelector(".User-Name"); 
let imageUserElement = root_element.querySelector(".Imageuser"); 

let defaultImageURL = "/files/Default_pfp.jpg";
let img = document.createElement("img");

function getGreeting() {
    let currentHour = new Date().getHours();

    if (currentHour < 12) {
        return "Good Morning";
    } else if (currentHour < 16) {
        return "Good Afternoon";
    } else {
        return "Good Evening";
    }
}

let greeting = getGreeting();

function updateUI(name, imageUrl) {

    userNameElement.innerHTML = "";

    let greetingSpan = document.createElement("span");
    greetingSpan.textContent = `${greeting},`;
    greetingSpan.style.display = "block";
    greetingSpan.style.fontFamily = "Times New Roman, serif";
    greetingSpan.style.color = "#000000"; // Dark black color

    let helloSpan = document.createElement("span");
    helloSpan.textContent = name;
    helloSpan.style.fontFamily = "Times New Roman, serif";
    helloSpan.style.color = "#000000"; // Dark black color

    userNameElement.appendChild(greetingSpan);
    userNameElement.appendChild(helloSpan);

    img.alt = "User Image";
    img.src = imageUrl;
    imageUserElement.innerHTML = "";
    imageUserElement.appendChild(img);
}

if (currentUser === "Administrator" || currentUser === "Guest") {
    updateUI(currentUser, defaultImageURL);
} else {
    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Employee",
            fields: ["employee_name", "image"],
            filters: { "user_id": currentUser }
        },
        callback: function(response) {
            if (response.message && response.message.length > 0) {
                let employeeName = response.message[0].employee_name;
                let employeeImage = response.message[0].image || defaultImageURL;
                updateUI(employeeName, employeeImage);
            } else {
                updateUI("Employee not found", defaultImageURL);
            }
        },
    });
}

style:
* {
  box-sizing: border-box;
}

html {
  font-family: sans-serif;
  font-weight: 300;
  color: #262626;
}

h2 {
  font-weight: 400;
  margin: 2rem 0 0.25rem 0;
}

h6, p {
  position: absolute;
  right: 20rem;
  margin: 0;
  color: black;
}

h6 {
  bottom: 2.5rem;
  opacity: 0.5;
  font-size: 24px;
}

p {
  bottom: 1rem;
  opacity: 0.5;
}

body {
  display: grid;
  place-items: center;
  min-height: 100vh;
}

figure {
  aspect-ratio: 8 / 2;
  background: #8cb4f99e;
  margin: 0;
  position: relative;
  overflow: hidden;
  border-radius: 1.5rem;
}

figure:after {
  content: "";
  position: absolute;
  width: 20%;
  aspect-ratio: 1;
  border-radius: 50%;
  right: 10%;
  background: linear-gradient(-65deg, var(--bg) 50%, var(--accent) 50%);
  filter: blur(25px);
  transform: translateX(calc(var(--hover) * 15%)) scale(calc(1 + (var(--hover) * 0.2)));
  transition: transform 0.2s, background 0.2s;
}

img {
  position: absolute;
  right: 10%;
  top: 15%;
  width: 140px;
  height: 140px;
  border-radius: 50%;
  object-fit: cover;
  transform: translateX(calc(var(--hover) * -15%)) scale(calc(1 + (var(--hover) * 0.2)));
  transition: transform 0.2s;
}

article {
  --hover: 0;
}

article:hover {
  --hover: 1;
}

script:
let attendance = root_element.querySelector(".data");

frappe.call({
    method: "frappe.client.get_list",
    args: {
        doctype: "Attendance",
        fields: ["name"], 
        filters: [
            ["attendance_date", "=", frappe.datetime.get_today()],
            ["status", "in", ["Present", "Draft"]]
        ]
    },
    callback: function (response) {
        if (response.message) {
            let count = response.message.length;
            attendance.textContent = count;
            console.log("Count of Present and Draft Attendance records for today: " + count);
        } else {
            attendance.textContent = 0;
            console.log("No records found or an error occurred.");
        }
    },
    error: function (error) {
        console.error("An error occurred while fetching attendance records:", error);
        attendance.textContent = "Error";
    }
});

