script:
let employeeInfo = root_element.querySelector(".employee-info");

if (employeeInfo) {
    frappe.call({
        method: "ezy_hr.ezy_hr.custom_script.common_script.birthday.get_employees_with_birthday_today",
        callback: function(response) {
            if (response.message) {
                if (response.message.length > 0) {
                    let employees = response.message;
                    let index = 0;
                    const defaultImage = "/files/Default_pfp.jpg"; // URL of the default image

                    function getEmployeeHTML(employee) {
                        let imageUrl = employee.image ? employee.image : defaultImage;
                        return `
                            <div style="text-align: center;">
                                <img src="${imageUrl}" alt="${employee.name}" style="max-height: 100px;   margin:auto; 
    height:100px;
    width:100px; border-radius:50%;object-fit:cover;">

                            </div>
                            <div style="text-align: center; padding-top:10px">${employee.name}</div>
                        `;
                    }

                    function displayNextEmployee() {
                        if (employees.length > 0) {
                            let employee = employees[index];
                            employeeInfo.innerHTML = getEmployeeHTML(employee);

                            index = (index + 1) % employees.length;
                        }
                    }

                    // Display the first employee immediately
                    displayNextEmployee();

                    // Set interval to display the next employee every 3 seconds
                    setInterval(displayNextEmployee, 3000);
                } else {
                    employeeInfo.innerHTML = "No birthdays today";
                }
            }
        }
    });
}

